import React, { useEffect, useState } from "react";

function App() {
  const [recipes, setRecipes] = useState([]);
  const [form, setForm] = useState({
    name: "",
    meal_type: "",
    ingredients: "",
    instructions: "",
    tags: ""
  });

  useEffect(() => {
    fetch("http://localhost:5000/recipes")
      .then(res => res.json())
      .then(data => setRecipes(data));
  }, []);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = e => {
    e.preventDefault();
    const newRecipe = {
      ...form,
      ingredients: form.ingredients.split(",").map(i => i.trim()),
      tags: form.tags.split(",").map(t => t.trim())
    };
    fetch("http://localhost:5000/recipes", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newRecipe)
    })
      .then(res => res.json())
      .then(data => {
        setRecipes([...recipes, { ...newRecipe, _id: data.id }]);
        setForm({
          name: "",
          meal_type: "",
          ingredients: "",
          instructions: "",
          tags: ""
        });
      });
  };

  return (
    <div style={{ maxWidth: 600, margin: "2rem auto", fontFamily: "sans-serif" }}>
      <h1>Meal Planner Recipes</h1>
      <form onSubmit={handleSubmit} style={{ marginBottom: "2rem" }}>
        <input name="name" placeholder="Name" value={form.name} onChange={handleChange} required /><br />
        <input name="meal_type" placeholder="Meal Type" value={form.meal_type} onChange={handleChange} required /><br />
        <input name="ingredients" placeholder="Ingredients (comma separated)" value={form.ingredients} onChange={handleChange} required /><br />
        <input name="instructions" placeholder="Instructions" value={form.instructions} onChange={handleChange} required /><br />
        <input name="tags" placeholder="Tags (comma separated)" value={form.tags} onChange={handleChange} /><br />
        <button type="submit">Add Recipe</button>
      </form>
      <ul>
        {recipes.map(r => (
          <li key={r._id}>
            <strong>{r.name}</strong> ({r.meal_type})<br />
            Ingredients: {r.ingredients.join(", ")}<br />
            Instructions: {r.instructions}<br />
            Tags: {r.tags.join(", ")}
            <hr />
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
